%% Clear and Close Figuresclear ; close all; clcfprintf('Loading data ...\n');%% Load Datadata = load('ex1data2.txt');X = data(:, 1:2);y = data(:, 3);m = length(y);% Print out some data pointsfprintf('First 15 examples from the dataset: \n');fprintf(' x = [%.0f %.0f], y = %.0f \n', [X(1:15,:) y(1:15,:)]');fprintf('Program paused. Press enter to continue.\n');% Scale features and set them to zero meanfprintf('Normalizing Features ...\n');[X mu sigma] = featureNormalize(X);% ---------------fprintf("[1 ; 2 ; 3]\n")[Xn mu sigma] = featureNormalize([1 ; 2 ; 3])% result%%Xn =%  -1%   0%   1%%mu =  2%sigma =  1%%----------------X = magic(3)[Xn mu sigma] = featureNormalize(X)% result%%Xn =%   1.13389  -1.00000   0.37796%  -0.75593   0.00000   0.75593%  -0.37796   1.00000  -1.13389%%mu =   5   5   5%sigma =   2.6458   4.0000   2.6458%%--------------[Xn mu sigma] = featureNormalize([-ones(1,3); magic(3)])% results%%Xn =%  -1.21725  -1.01472  -1.21725%   1.21725  -0.56373   0.67625%  -0.13525   0.33824   0.94675%   0.13525   1.24022  -0.40575%%mu =%   3.5000   3.5000   3.5000%%sigma =%  3.6968   4.4347   3.6968%